<script setup lang="ts">
import { ref } from 'vue';
import NoLogin from '../../components/NoLogin/NoLogin.vue';
import Taro from '@tarojs/taro';

const isLogin = ref(true);
const collectedHouses = ref([
  {
    id: 1,
    image: 'data:image/jpeg;base64,',
    title: '海景大床房',
    price: 299,
    tags: ['海景', '可做饭', '独立卫浴']
  },
  {
    id: 2,
    image: 'data:image/jpeg;base64,',
    title: '豪华套房',
    price: 499,
    tags: ['套房', '阳台', '市中心']
  }
]);

const removeCollect = (id: number) => {
  collectedHouses.value = collectedHouses.value.filter(house => house.id !== id);
  Taro.showToast({
    title: '已取消收藏',
    icon: 'success'
  });
};

const navToHome = () => {
  Taro.switchTab({
    url: '/pages/index/index'
  });
};
</script>

<template>
  <view class="page-container">
    <template v-if="isLogin">
      <view class="collect-list">
        <view v-for="house in collectedHouses" :key="house.id" class="house-card">
          <image :src="house.image" class="house-image" />
          <view class="house-info">
            <text class="house-title">{{ house.title }}</text>
            <view class="tag-list">
              <text v-for="(tag, index) in house.tags" :key="index" class="tag">{{ tag }}</text>
            </view>
            <view class="price-action">
              <text class="house-price">¥{{ house.price }}/晚</text>
              <at-button type="secondary" size="small" @click="removeCollect(house.id)">取消收藏</at-button>
            </view>
          </view>
        </view>
      </view>
      <view v-if="collectedHouses.length === 0" class="empty-state">
        <image class="empty-image" src="https://img12.360buyimg.com/jdphoto/s72x72_jfs/t6160/14/2008729947/2754/7d512a86/595c3aeeNa89ddf71.png" />
        <text class="empty-text">暂无收藏的房源</text>
        <text class="empty-subtext">去首页发现心仪的房源吧</text>
        <button class="explore-button" @click="navToHome">去逛逛</button>
      </view>
    </template>
    <template v-else>
      <NoLogin />
    </template>
  </view>
</template>

